INSERT INTO creature_text VALUES 
(29519, 0, 0, "There can be only one survivor!", 12, 0, 100, 1, 0, 0, "Unworthy Initiate to ", 30204), 
(29519, 0, 1, "And now you die", 12, 0, 100, 0, 0, 0, "unworthy initiate SAY_EVENT_ATTACK_4", 30205), 
(29519, 0, 2, "I will win my freedom and leave this cursed place!", 12, 0, 100, 0, 0, 0, "unworthy initiate SAY_EVENT_START_6", 30207), 
