(29519, 0, 4, "They brand me unworthy? I will show them unmorthy!", 12, 0, 100, 0, 0, 0, "unworthy initiate SAY_EVENT_START_4", 30209), 
(29519, 0, 5, "I was a soldier of the Light once... Look at what I have become... ", 12, 0, 100, 0, 0, 0, "unworthy initiate SAY_EVENT_START_2", 30210), 
