(29519, 1, 1, "Death is the only cure!", 12, 0, 100, 0, 0, 0, "unworthy initiate SAY_EVENT_ATTACK_9", 30215), 
(29519, 1, 2, "There is no hope for our future...", 12, 0, 100, 0, 0, 0, "unworthy initiate SAY_EVENT_ATTACK_6", 30216), 
(29519, 1, 3, "To battle!", 12, 0, 100, 0, 0, 0, "unworthy initiate SAY_EVENT_ATTACK_1", 30218), 
