INSERT INTO creature_text VALUES 
(28912, 0, 0, "Damn the Crusade! I think my ribs are broken and I might be bleeding internally.", 12, 0, 100, 1, 0, 0, "Koltira Deathweaver to ", 29197), 
(28912, 1, 0, "I'll need to get my runeblade and armor... Just need a little more time.", 12, 0, 100, 0, 0, 0, "Koltira Deathweaver to ", 29201), 
(28912, 2, 0, "I'm still weak, but I think I can get an anti-magic barrier up. Stay inside it or you'll be destroyed by their spells.", 12, 0, 100, 0, 0, 0, "Koltira Deathweaver to ", 29203), 
(28912, 3, 0, "Maintaining this barrier will require all of my concentration. Kill them all!", 12, 0, 100, 0, 0, 0, "Koltira Deathweaver to ", 29205), 
(28912, 4, 0, "There are more coming. Defend yourself! Don't fall out of the anti-magic field! They'll tear you apart without its protection!", 12, 0, 100, 0, 0, 0, "Koltira Deathweaver to ", 29207), 
(28912, 5, 0, "I can't keep this barrier up much longer... Where is that coward?", 12, 0, 100, 0, 0, 0, "Koltira Deathweaver to ", 29208), 
(28912, 6, 0, "The High Inquisitor comes! Be ready, death knight! Do not let him draw you out of the protective bounds of my anti-magic field! Kill him and take his head!", 12, 0, 100, 0, 0, 0, "Koltira Deathweaver to ", 29210), 
(28912, 7, 0, "Stay in the anti-magic field! Make them come to you!", 12, 0, 100, 0, 0, 0, "koltira deathweaver SAY_BREAKOUT7", 29225), 
(28912, 8, 0, "The death of the High Inquisitor of New Avalon will not go unnoticed. You need to get out of here at once! Go, before more of them show up. I'll be fine on my own.", 12, 0, 100, 1, 0, 0, "Koltira Deathweaver to High Inquisitor Valroth", 29239), 
(28912, 9, 0, "I'll draw their fire, you make your escape behind me.", 12, 0, 100, 1, 0, 0, "Koltira Deathweaver to High Inquisitor Valroth", 29240), 
(28912, 10, 0, "Your High Inquisitor is nothing more than a pile of meat, Crusaders! There are none beyond the grasp of the Scourge!", 14, 0, 100, 22, 0, 0, "Koltira Deathweaver to High Inquisitor Valroth", 29241);
