INSERT INTO creature_text VALUES 
(29173, 0, 0, "Soldiers of the Scourge, stand ready! Prepare to unleash your fury upon the Argent Dawn!", 14, 0, 100, 0, 0, 14677, "Highlord Darion Mograine", 29694), 
(29173, 1, 0, "The sky weeps at the glorious devastation of these lands! Soon, Azeroth's futile tears will rain down upon us!", 14, 0, 100, 0, 0, 14678, "Highlord Darion Mograine", 29696), 
(29173, 2, 0, "Death knights of Acherus, the death march begins!", 14, 0, 100, 0, 0, 14681, "Highlord Darion Mograine", 29697), 
(29173, 3, 0, "Soldiers of the Scourge, death knights of Acherus, minions of the darkness: hear the call of the Highlord!", 14, 0, 100, 22, 0, 14679, "Highlord Darion Mograine to ", 29699), 
(29173, 4, 0, "RISE!", 14, 0, 100, 15, 0, 14680, "Highlord Darion Mograine to ", 29700), 
(29173, 5, 0, "The skies turn red with the blood of the fallen! The Lich King watches over us, minions! Leave only ashes and misery in your destructive wake!", 14, 0, 100, 0, 0, 0, "Highlord Darion Mograine to ", 29722), 
(29173, 7, 0, "Rise, minions! Destroy them!", 14, 0, 100, 0, 0, 0, "Highlord Darion Mograine to Defender of the Light", 29726), 
(29173, 8, 0, "Kneel before the Highlord!", 12, 0, 100, 0, 0, 14683, "Highlord Darion Mograine", 29727), 
(29173, 8, 1, "You stand no chance!", 12, 0, 100, 0, 0, 14684, "Highlord Darion Mograine", 29728), 
(29173, 8, 2, "The Scourge will destroy this place!", 12, 0, 100, 0, 0, 14685, "Highlord Darion Mograine", 29729),
(29173, 8, 3, "Your life is forfeit.", 12, 0, 100, 0, 0, 14686, "Highlord Darion Mograine", 29730), 
(29173, 8, 4, "Life is meaningless without suffering.", 12, 0, 100, 0, 0, 14687, "Highlord Darion Mograine", 29731), 
(29173, 8, 5, "How much longer will your forces hold out?", 12, 0, 100, 0, 0, 14688, "Highlord Darion Mograine", 29732), 
(29173, 8, 6, "The Argent Dawn is finished!", 12, 0, 100, 0, 0, 14689, "Highlord Darion Mograine", 29733), 
(29173, 8, 7, "Spare no one!", 12, 0, 100, 0, 0, 14690, "Highlord Darion Mograine", 29734), 
(29173, 8, 8, "What is this?! My... I cannot strike...", 12, 0, 100, 0, 0, 14691, "Highlord Darion Mograine", 29735), 
(29173, 8, 9, "Obey me, blade!", 14, 0, 100, 0, 0, 14692, "Highlord Darion Mograine", 29736), 
(29173, 8, 10, "You will do as I command! I am in control here!", 12, 0, 100, 0, 0, 14693, "Highlord Darion Mograine", 29737), 
(29173, 8, 11, "I can not... the blade fights me.", 12, 0, 100, 0, 0, 14694, "Highlord Darion Mograine", 29738), 
(29173, 8, 12, "What is happening to me?", 12, 0, 100, 0, 0, 14695, "Highlord Darion Mograine", 29739), 
(29173, 8, 13, "Power...wanes...", 12, 0, 100, 0, 0, 14696, "Highlord Darion Mograine", 29740), 
(29173, 8, 14, "Ashbringer defies me...", 12, 0, 100, 0, 0, 14697, "Highlord Darion Mograine", 29741), 
(29173, 8, 15, "Minions, come to my aid!", 12, 0, 100, 0, 0, 14698, "Highlord Darion Mograine", 29742), 
(29173, 30, 0, "%s kneels in defeat before Tirion Fordring.", 16, 0, 100, 0, 0, 0, "Highlord Darion Mograine", 29759), 
(29173, 31, 0, "Stand down, death knights. We have lost... The Light... This place... No hope...", 12, 0, 100, 0, 0, 14699, "Highlord Darion Mograine", 29760), 
(29173, 32, 0, "Save your breath, old man. It might be the last you ever draw.", 12, 0, 100, 0, 0, 14700, "Highlord Darion Mograine", 29761), 
(29173, 33, 0, "Father!", 12, 0, 100, 0, 0, 14701, "Highlord Darion Mograine", 29767), 
(29173, 34, 0, "Argh...what...is...", 12, 0, 100, 0, 0, 14702, "Highlord Darion Mograine", 29768), 
(29173, 35, 0, "You have've betrayed me! You betrayed us all you monster! Face the might of Mograine!", 14, 0, 100, 0, 0, 14707, "Highlord Darion Mograine", 29781), 
(29173, 36, 0, "Tirion!", 14, 0, 100, 0, 0, 14709, "Highlord Darion Mograine", 29801), 
(29173, 37, 0, "%s collapses.", 16, 0, 100, 0, 0, 0, "Highlord Darion Mograine", 29802), 
(29173, 38, 0, "That day is not today...", 12, 0, 100, 0, 0, 14708, "Highlord Darion Mograine", 29824), 
(29173, 39, 0, "So too do the Knights of the Ebon Blade. Although our kind has no place in your world, we will fight to bring an end to the Lich King. This I vow!", 12, 0, 100, 0, 0, 14710, "Highlord Darion Mograine", 29851);
INSERT INTO creature_text VALUES 
(29175, 0, 0, "You cannot win, Darion!", 14, 0, 100, 22, 0, 14584, "Highlord Tirion Fordring to Highlord Darion Mograine", 29743), 
(29175, 1, 0, "Have you learned nothing, boy? You have become all that your father fought against! Like that coward, Arthas, you allowed yourself to be consumed by the darkness...the hate... Feeding upon the misery of those you tortured and killed!", 12, 0, 100, 0, 0, 14586, "Highlord Tirion Fordring", 29744), 
(29175, 2, 0, "Your master knows what lies beneath the chapel. That is why he dares not show his face! He's sent you and your death knights to meet their doom, Darion.", 12, 0, 100, 25, 0, 14587, "Highlord Tirion Fordring to Highlord Darion Mograine", 29745), 
(29175, 3, 0, "What you are feeling right now is the anguish of a thousand lost souls! Souls that you and your master brought here! The Light will tear you apart, Darion!", 12, 0, 100, 1, 0, 14588, "Highlord Tirion Fordring to Highlord Darion Mograine", 29746), 
(29175, 4, 0, "Bring them before the chapel!", 14, 0, 100, 22, 0, 14585, "Highlord Tirion Fordring to Highlord Darion Mograine", 29756), 
(29175, 5, 0, "You're a damned monster, Arthas!", 12, 0, 100, 25, 0, 14589, "Highlord Tirion Fordring to The Lich King", 29783), 
(29175, 6, 0, "%s gasps for air.", 16, 0, 100, 0, 0, 14590, "Highlord Tirion Fordring to The Lich King", 29786), 
(29175, 7, 0, "ARTHAS!!!!", 14, 0, 100, 15, 0, 14591, "Highlord Tirion Fordring to Highlord Darion Mograine", 29803), 
(29175, 8, 0, "Rise, Darion, and listen...", 12, 0, 100, 0, 0, 14593, "Highlord Tirion Fordring", 29835), 
(29175, 9, 0, "We have all been witness to a terrible tragedy. The blood of good men has been shed upon this soil! Honorable knights, slain defending their lives -- our lives!", 12, 0, 100, 0, 0, 14594, "Highlord Tirion Fordring to Highlord Tirion Fordring", 29843), 
(29175, 10, 0, "And while such things can never be forgotten, we must remain vigilant in our cause!", 12, 0, 100, 0, 0, 14595, "Highlord Tirion Fordring to Highlord Tirion Fordring", 29845), 
(29175, 11, 0, "The Lich King must answer for what he has done, and must not be allowed to cause further destruction in our world.", 12, 0, 100, 0, 0, 14596, "Highlord Tirion Fordring to Highlord Tirion Fordring", 29846), 
(29175, 12, 0, "I make a promise to you now, brothers and sisters: the Lich King will be defeated! On this day, I call for a union.", 12, 0, 100, 0, 0, 14597, "Highlord Tirion Fordring to Highlord Tirion Fordring", 29847), 
(29175, 13, 0, "The Argent Dawn and the Order of the Silver Hand will come together as one! We will succeed where so many before us have failed!", 12, 0, 100, 0, 0, 14598, "Highlord Tirion Fordring to Highlord Tirion Fordring", 29848), 
(29175, 14, 0, "We will take the fight to Arthas, and we will tear down the walls of Icecrown!", 12, 0, 100, 15, 0, 14599, "Highlord Tirion Fordring to Highlord Tirion Fordring", 29849), 
(29175, 15, 0, "The Argent Crusade comes for you, Arthas!", 14, 0, 100, 15, 0, 14600, "Highlord Tirion Fordring to Highlord Tirion Fordring", 29850), 
(29175, 16, 0, "Your end.", 14, 0, 100, 0, 0, 14592, "Highlord Tirion Fordring to Highlord Darion Mograine", 29861);
