(29181, 0, 0, "%s falls unconscious.", 41, 0, 100, 0, 0, 0, "Rayne to ", 29725);
INSERT INTO creature_text VALUES 
(29183, 0, 0, "Do not forget...", 14, 0, 100, 0, 0, 14803, "The Lich King to Highlord Alexandros Mograine", 29778), 
(29183, 0, 1, "Touching...", 14, 0, 100, 0, 0, 14803, "The Lich King", 29779), 
(29183, 0, 2, "He is mine now...", 12, 0, 100, 0, 0, 14805, "The Lich King to Highlord Alexandros Mograine", 29780), 
(29183, 0, 3, "Pathetic...", 12, 0, 100, 0, 0, 14804, "The Lich King to Highlord Darion Mograine", 29782), 
(29183, 0, 4, "You were right, Fordring. I did send them in to die. Their lives are meaningless, but yours...", 12, 0, 100, 396, 0, 14806, "The Lich King to The Lich King", 29784), 
(29183, 0, 5, "How simple it was to draw the great Tirion Fordring out of hiding. You've left yourself exposed, paladin. Nothing will save you.", 12, 0, 100, 1, 0, 14807, "The Lich King to The Lich King", 29785), 
(29183, 0, 6, "APOKALYPSE!", 14, 0, 100, 0, 0, 14808, "The Lich King to ", 29788), 
(29183, 0, 7, "What is this?", 14, 0, 100, 0, 0, 14809, "The Lich King", 29804), 
(29183, 0, 8, "Impossible...", 14, 0, 100, 0, 0, 14810, "The Lich King to Highlord Tirion Fordring", 29825), 
(29183, 0, 9, "This... is not over!", 14, 0, 100, 397, 0, 14811, "The Lich King to Highlord Tirion Fordring", 29826), 
(29183, 0, 10, "When next we meet, it won't be on holy ground, paladin.", 14, 0, 100, 1, 0, 14812, "The Lich King to Highlord Tirion Fordring", 29827);
INSERT INTO creature_text VALUES 
(29204, 0, 0, "%s flees.", 16, 0, 100, 0, 0, 0, "Orbaz Bloodbane to Highlord Darion Mograine", 29758);
INSERT INTO creature_text VALUES 
(29227, 0, 0, "%s arrives.", 16, 0, 100, 0, 0, 0, "Highlord Alexandros Mograine", 1415), 
(29227, 1, 0, "My son! My dear, beautiful boy!", 12, 0, 100, 5, 0, 14493, "Highlord Alexandros Mograine to Highlord Darion Mograine", 29762), 
(29227, 2, 0, "Nothing could have kept me away from here, Darion. Not from my home and family.", 12, 0, 100, 1, 0, 14494, "Highlord Alexandros Mograine to Highlord Alexandros Mograine", 29773), 
(29227, 3, 0, "Darion Mograine, you are barely of age to hold a sword, let alone battle the undead hordes of Lordaeron! I could not bear losing you. Even the thought...", 12, 0, 100, 1, 0, 14495, "Highlord Alexandros Mograine to Highlord Alexandros Mograine", 29775), 
(29227, 4, 0, "My son, there will come a day when you will command the Ashbringer and, with it, mete out justice across this land. I have no doubt that when that day finally comes, you will bring pride to our people and that Lordaeron will be a better place because of you. But, my son, that day is not today.", 12, 0, 100, 1, 0, 14496, "Highlord Alexandros Mograine to Highlord Alexandros Mograine", 29777), 
(29227, 5, 0, "Do not forget...", 12, 0, 100, 1, 0, 14497, "Highlord Alexandros Mograine to Highlord Alexandros Mograine", 29778);
INSERT INTO creature_text VALUES 
(29228, 0, 0, "Father, you have returned!", 12, 0, 100, 1, 0, 14703, "Darion Mograine to Highlord Alexandros Mograine", 29769), 
(29228, 1, 0, "You have been gone a long time, Father. I thought...", 12, 0, 100, 1, 0, 14704, "Darion Mograine to Highlord Alexandros Mograine", 29770), 
(29228, 2, 0, "%s hugs his father.", 16, 0, 100, 0, 0, 0, "Darion Mograine to Highlord Alexandros Mograine", 29771), 
(29228, 3, 0, "Father, I wish to join you in the war against the undead. I want to fight! I can sit idle no longer!", 12, 0, 100, 1, 0, 14705, "Darion Mograine to Highlord Alexandros Mograine", 29774), 
(29228, 4, 0, "If I die, Father, I would rather it be on my feet, standing in defiance against the undead legions! If I die, let me die with you!", 12, 0, 100, 1, 0, 14706, "Darion Mograine to Highlord Alexandros Mograine", 29776);
INSERT INTO creature_text VALUES 
(29611, 0, 0, "People of Stormwind! Citizens of the Alliance! Your king speaks!", 14, 7, 0, 22, 0, 0, "King Varian Wrynn to ", 31675), 
(29611, 1, 0, "Der heutige Tag bringt die erste von vielen Niederlagen der Geiel! Todesritter, einst im Dienste des Lichknigs, haben sich von seinem Griff befreit und eine neue Allianz gegen seine Tyrannei gebildet!", 14, 7, 0, 22, 0, 0, "King Varian Wrynn to ", 31676), 
(29611, 2, 0, "You will welcome these former heroes of the Alliance and treat them with the respect that you would give to any ally of Stormwind!", 14, 7, 0, 22, 0, 0, "King Varian Wrynn to ", 31677), 
(29611, 3, 0, "Glory to the Alliance!", 14, 7, 0, 71, 0, 0, "King Varian Wrynn to ", 31678);
